import './App.css';
import { Component } from 'react';
import MainSearch from './components/MainSearch';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    fetch('https://sports.api.decathlon.com/sports')
      .then(res => res.json())
      .then(data => this.setState({ data: data['data'] }, () => console.log(this.state.data)));
  }

  render() {
    const { data } = this.state;
    return (
      <div className="app">
        <header>
          <h1 className="header-title">
            Le<br />
            Wiki<br />
            des<br />
            Sports</h1>
        </header>

        <MainSearch
          data={ data } />
      </div>
    );
  }
}

export default App;
