import { VtmnIcon, VtmnTextInput, VtmnButton } from '@vtmn/react';

const MainSearch = props => {
	return (
		<section className="search-box-results">
	      <div className="search-box">

	        <div className="textInput">
	          <img
	            src="https://img.icons8.com/ios/50/000000/search--v1.png"
	            className="search-icon" />
	          <VtmnTextInput
	            icon="search-line"
	            placeholder="Recherchez"
	            value={ searchInput }
	            onChange={ e => this.setState({ searchInput: e.target.value }) }></VtmnTextInput>
	        </div>
	      </div>
	      { (props.searchInput.length > 0 && document.getElementsByClassName('vtmn-text-input')[0] === document.activeElement) ? resultList : null }

	      <p className="intro-p">
	        Bonjour ! Bienvenue sur le Wiki des sports ! Retrouvez-ici les infos relatives à tous les sports répertoriés.<br />
	        Effectuez une recherche pour essayer dès maintenant =)
	      </p>
	    </section> );
}

export default MainSearch;
