import './App.css';
import { Component } from 'react';
import { VtmnTextInput, VtmnButton } from '@vtmn/react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchInput: '',
      data: [],
    };
  }

  componentDidMount() {
    fetch('https://sports.api.decathlon.com/sports')
      .then(res => res.json())
      .then(data => this.setState({ data: data['data'] }, () => console.log(this.state.data)));
  }

  render() {
    const { searchInput, data } = this.state;

    const resultList = <ul className="inputList">
      {
        data.filter(val => {
          if (val['attributes']['name']) {
            if (val['attributes']['name'].toLowerCase().includes(searchInput.toLowerCase()))
              return val;
          }
        }).map((e, i) => (
          <li key={i}>
            { e['attributes']['name'] }
          </li>
        ))
      }
    </ul>;

    return (
      <header>

        <nav>
        <div className="search-box">
          <div>
            <VtmnTextInput
              icon="search-line"
              placeholder="Recherchez"
              labelText="Effectuez une recherche"
              value={ searchInput }
              onChange={ e => this.setState({ searchInput: e.target.value }) }></VtmnTextInput>
          </div>
          { (searchInput.length > 0) ? resultList : null }
        </div>

        </nav>
      </header>
    );
  }
}

export default App;
