import './MainSearch.css';
import { useState } from 'react';
import { VtmnIcon, VtmnTextInput, VtmnButton } from '@vtmn/react';

const MainSearch = props => {
	const [searchInput, setSearchInput] = useState('');

    const resultList = <ul className="inputList">
	      {
	        props.data.filter(val => {
	          if (val['attributes']['name']) {
	            if (val['attributes']['name'].toLowerCase().includes(searchInput.toLowerCase()))
	              return val;
	          }
	        }).map((e, i) => (
	          <li
	          	key={ i }
	          	onClick={ () => props.setSportData(e) }>
	            { e['attributes']['name'] }
	          </li>
	        ))
	      }
	    </ul>;

	return (
		<section className="search-box-results">
	      <SearchBar />

	      <p className="intro-p">
	        Bonjour ! Bienvenue sur le Wiki des sports ! Retrouvez-ici les infos relatives à tous les sports répertoriés.<br />
	        Effectuez une recherche pour essayer dès maintenant =)
	      </p>
	    </section> );
}

export default MainSearch;
