import './App.css';
import { Component } from 'react';
import MainSearch from './components/MainSearch';
import PageView from './components/PageView';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      actualSportData: -1,
    };
  }

  componentDidMount = () => {
    fetch('https://sports.api.decathlon.com/sports')
      .then(res => res.json())
      .then(data => this.setState({ data: data['data'] }, () => console.log(this.state.data)));
  }

  setSportData = actualSportData => this.setState({ actualSportData }, () => console.log(actualSportData));

  render() {
    const { data, actualSportData } = this.state;

    return (
      <div className="app">
        <header>
          <h1
            className="header-title"
            style={ actualSportData !== -1 ? { backgroundColor: "#0082C3" } : null }>
            Le<br />
            Wiki<br />
            des<br />
            Sports</h1>
        </header>

        {
          (actualSportData === -1) ?
            <MainSearch
              data={ data }
              setSportData={ this.setSportData } />
          :
          <PageView
            actualSportData={ actualSportData } />
        }
      </div>
    );
  }
}

export default App;
