import './App.css';
import { Component } from 'react';
import MainSearch from './components/MainSearch';
import PageView from './components/PageView';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      actualPageId: -1,
    };
  }

  componentDidMount() {
    fetch('https://sports.api.decathlon.com/sports')
      .then(res => res.json())
      .then(data => this.setState({ data: data['data'] }, () => console.log(this.state.data)));
  }

  render() {
    const { data, actualPage } = this.state;

    return (
      <div className="app">
        <header>
          <h1 className="header-title">
            Le<br />
            Wiki<br />
            des<br />
            Sports</h1>
        </header>

        {
          actualPage === 'home' ?
            <MainSearch
              data={ data } />
          :
          <PageView
            actualPageId={ actualPageId } />
        }
      </div>
    );
  }
}

export default App;
